var layouts=["background","hero","profile","page","card"],list_config,titles,currentConfig,currentLayout=0;function switchHomeLayout(){var e=currentLayout,t,n;currentLayout=currentLayout==layouts.length-1?0:currentLayout+1,t=document.getElementById(layouts[e]),n=document.getElementById(layouts[currentLayout]);const s=document.querySelectorAll("code[id=layout]");n.style.display="block",t.style.display="none",s.forEach(function(e){e.innerText=layouts[currentLayout]})}window.addEventListener("DOMContentLoaded",e=>{document.querySelectorAll("#switch-layout-button").forEach(e=>e.addEventListener("click",function(e){e.preventDefault(),switchHomeLayout()}))}),list_config=["CardViewProse","CardViewScreenWidth","NormalView"],titles={CardViewProse:"card view with constrained width",CardViewScreenWidth:"card view with full width",NormalView:"standard list view"},currentConfig=0;function switchList(){var e=currentConfig,t,n;currentConfig=currentConfig==list_config.length-1?0:currentConfig+1,t=document.getElementById(list_config[e]),n=document.getElementById(list_config[currentConfig]);const s=document.querySelectorAll("code[id=config]");n.style.display="block",t.style.display="none",s.forEach(function(e){e.innerText=titles[list_config[currentConfig]]})}window.addEventListener("DOMContentLoaded",e=>{document.querySelectorAll("#switch-config-button").forEach(e=>e.addEventListener("click",function(e){e.preventDefault(),switchList()}))})