if(typeof auth!="undefined"){var viewsCollection=db.collection("views"),update_views,update_likes,likesCollection=db.collection("likes");function numberWithCommas(e){return e.toString().replace(/\B(?=(\d{3})+(?!\d))/g,",")}update_views=function(e,t){viewsCollection.doc(t).onSnapshot(n=>{var t=n.data();t&&(e.innerText=numberWithCommas(t.views))})},update_likes=function(e,t){likesCollection.doc(t).onSnapshot(n=>{var t=n.data();t&&(e.innerText=numberWithCommas(t.likes))})},auth.signInAnonymously().then(()=>{var s=document.querySelectorAll("span[id^='views_']"),n,e,t,o;for(n in s)e=s[n],t=e.id?e.id.replaceAll("/","-"):e.id,t&&update_views(e,t);o=document.querySelectorAll("span[id^='likes_']");for(n in o)e=o[n],t=e.id?e.id.replaceAll("/","-"):e.id,t&&update_likes(e,t)}).catch(e=>{var t=e.code,n=e.message;console.error(t,n)})}